const homeTestimonialsMain=document.querySelector(".testimonials-main"),homeTestimonialContainer=document.querySelector(".testimonials-container"),homeTestimonialButton=document.querySelector(".hometestimonial-button"),homeTestimonialButtonText=document.querySelector(".hometestimonial-button-text");homeTestimonialButton.addEventListener("click",(()=>{homeTestimonialContainer.classList.contains("viewfour")?(homeTestimonialContainer.classList.remove("viewfour"),homeTestimonialsMain.classList.remove("viewfour"),homeTestimonialButtonText.textContent="View More"):(homeTestimonialContainer.classList.add("viewfour"),homeTestimonialsMain.classList.add("viewfour"),homeTestimonialButtonText.textContent="View Less")}));