function submitNewsletterDetails(){const e=document.querySelector("#newsletter-email").value,t=document.querySelector("#newsletter-email"),s=document.querySelector(".newsletter-subscription-thankyou"),r=document.querySelector(".subscription-error-message");if(""===e)return t.classList.add("error"),r.classList.add("active"),void(r.textContent="Please enter your email");t.classList.remove("error"),r.classList.remove("active"),r.textContent="";if(!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(e))return t.classList.add("error"),r.classList.add("active"),void(r.textContent="Please enter a valid email address");t.classList.remove("error"),r.classList.remove("active"),r.textContent="";const n={email:e},a=document.querySelector("[name=csrfmiddlewaretoken]").value;fetch("https://www.reckonsys.com/backend/news-letter",{method:"POST",body:JSON.stringify(n),headers:{"Content-Type":"application/json","X-CSRFToken":a}}).then((e=>e.json())).then((e=>{s.classList.add("active")})).catch((e=>{}))}const submitEmailBtn=document.querySelector(".newsletter-submit");submitEmailBtn.addEventListener("click",(e=>{e.preventDefault(),submitNewsletterDetails()}));