const welcomebackCloseBtn=document.querySelector(".welcomeback-close-btn"),welcomebackContainer=document.querySelector(".body-container-welcomeback-model");document.addEventListener("mouseout",(function(e){e.clientY<=0&&(document.cookie.split(";").some((e=>e.trim().startsWith("seenMessage=")))||welcomebackContainer.classList.remove("remove"))})),welcomebackCloseBtn.addEventListener("click",(()=>{welcomebackContainer.classList.add("remove"),document.cookie="seenMessage=true; expires="+new Date(Date.now()+72e5).toUTCString()+"; path=/"}));